import React from 'react'

class Cart extends React.Component
{
    constructor(props)
    {
        super(props)

        
        this.state = {
            games: this.props.games,
            finalprice: 0
        }

        this.calculatePrice()
    }

    calculatePrice()
    {
        let final_price = 0;

        for (let i = 0; i < this.props.games.length; i++)
        {
            final_price += this.props.games[i].price
        }

        this.state.finalprice = final_price;
        this.setState(this.state)
    }

    showBuyMessage()
    {
        this.state.games.splice(0, this.state.games.length)
        this.setState(this.state)

        return (
            <div>
            {alert('Успешно!')}
            {this.render()}
            </div>
        );
    }

    deleteFromCart(index)
    {
        this.state.games.splice(index, 1)
        this.setState(this.state) // Only place you can directly write to this.state should be the 
                                  // Components constructor. In all the other places you should be using this.setState function,
                                  // which will accept an Object that will be eventually merged into Components current state.

        this.calculatePrice()

        return (
            this.render()
        );
    }

    render()
    {
        if ( this.state.games.length == 0 )
        {
            return (
                <div class = "Cart">
                    <p>Корзина пуста</p>
                </div>
            )
        }
        else
        {
            return (
                <div class = "Cart">
                    <p>В корзине игр: {this.state.games.length}</p>
    
                    {this.state.games.map((game, index) => (
                        <div class = "game">
                            <p>
                                {game.name}
                            </p>
    
                            <p>
                                Цена: {game.price}
                            </p>
        
                            <button onClick={() => { this.deleteFromCart(index) }}>
                                Убрать из корзины
                            </button>               
                        </div>
                    ))}

                    <p>
                        Финальная цена: {this.state.finalprice}
                    </p>
    
                    <form method="get" action="/buy">
                        <button onClick={() => { this.showBuyMessage() }}>
                         Купить
                        </button>
                    </form>
                </div>
            );
        }
    }
}

export default Cart
