import React from 'react';
import logo from './logo.svg';
import './App.css';

import Cart from './components/cart'


function showBuyMessage()
{
    return
    (
        alert('Success!')
    )
}


function App() 
{
  const games  = [{name: 'Red Dead Redemption 2'}, 'Ruiner', 'Untitled Goose Game']
  const prices = [2499, 0, 499]
  let finalprice = 0

  for (let i = 0; i < prices.length; i++)
  {
    finalprice += prices[i];
  }

  return (
    <div className="App">

      <p>В корзине игр: {games.length}</p>
      <Cart>
        {
          games.map()
        }
      </Cart>
      <p>Финальная цена: {finalprice}</p>

      <form method="get" action="/buy">
        <button onClick={showBuyMessage()}>
          Купить
        </button>
      </form>

    </div>
  );
}

export default App;
