import React from 'react'

class Cart extends React.Component
{
    constructor(props)
    {
        super(props)

        this.state = {
            games: this.props.games
        }
    }

    showBuyMessage()
    {
        return (
            alert('Success!')
        );
    }

    deleteFromCart(index)
    {
        this.state.games.splice(index, 1)
        this.setState(this.state) // Only place you can directly write to this.state should be the Components constructor (or, if you’re using class-properties plugin a babel-preset, the class declaration). In all the other places you should be using this.setState function, which will accept an Object that will be eventually merged into Components current state.

        return (
            this.render()
        );
    }

    render()
    {
        if ( this.state.games.length == 0 )
        {
            return (
                <div class = "Cart">
                    <p>Корзина пуста</p>
                </div>
            )
        }
        else
        {
            return (
                <div class = "Cart">
                    <p>В корзине игр: {this.state.games.length}</p>
    
                    {this.state.games.map((game, index) => (
                        <div class = "game">
                            <p>
                                {game.name}
                            </p>
    
                            <p>
                                Цена: {game.price}
                            </p>
        
                            <button onClick={() => { this.deleteFromCart(index) }}>
                                Убрать из корзины
                            </button>               
                        </div>
                    ))}
    
                    <form method="get" action="/buy">
                        <button onClick={() => { this.showBuyMessage() }}>
                         Купить
                        </button>
                    </form>
                </div>
            );
        }
    }
}

export default Cart
