  const prices = [2499, 0, 499]
  finalprice = 0

  for (let i = 0; i < prices.length; i++)
  {
    finalprice += prices[i];
  }

  let showMessage = showBuyMessage()

  return (
    <div className="App">

      <p>В корзине игр: {games.length}</p>
      <Cart>{games, prices}</Cart>
      <p>Финальная цена: {finalprice}</p>

      <form method="get" action="/buy">
        <button onClick={showB}>
          Купить
        </button>
      </form>

    </div>
  );
}

export default App;
