        {
            <div class = "Cart">
                <p>В корзине игр: {this.mygames.length}</p>

                <div class = "game">
                    <p>
                        {game.name}
                    </p>
    
                    <p>
                        {game.price}
                    </p>
    
                    <button onClick={this.deleteFromCart()}>
                        Убрать из корзины
                    </button>               
                </div>

                <form method="get" action="/buy">
                    <button onClick={this.showBuyMessage()}>
                     Купить
                    </button>
                </form>
            </div>
        }
        );
    }
}

export default Cart
